import React, { useState } from 'react';
import { Trash2, Plus, Check } from 'lucide-react';

export default function DailyPlanner() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');
  const [newPriority, setNewPriority] = useState('medium');
  const [newDeadline, setNewDeadline] = useState('');
  const [newTime, setNewTime] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);

  const addTask = () => {
    if (newTask.trim() && newDeadline && newTime) {
      const task = {
        id: Date.now(),
        text: newTask,
        priority: newPriority,
        deadline: newDeadline,
        time: newTime,
        completed: false,
        date: selectedDate
      };
      setTasks([...tasks, task]);
      setNewTask('');
      setNewDeadline('');
      setNewTime('');
      setNewPriority('medium');
    }
  };

  const toggleTask = (id) => {
    setTasks(tasks.map(task => task.id === id ? { ...task, completed: !task.completed } : task));
  };

  const deleteTask = (id) => {
    setTasks(tasks.filter(task => task.id !== id));
  };

  const todayTasks = tasks.filter(task => task.date === selectedDate).sort((a, b) => {
    const priorityOrder = { high: 0, medium: 1, low: 2 };
    return priorityOrder[a.priority] - priorityOrder[b.priority];
  });

  const completedCount = todayTasks.filter(t => t.completed).length;
  const completionRate = todayTasks.length > 0 ? Math.round((completedCount / todayTasks.length) * 100) : 0;

  const getPriorityColor = (priority) => {
    const colors = {
      high: 'bg-red-100 border-l-4 border-red-500',
      medium: 'bg-yellow-100 border-l-4 border-yellow-500',
      low: 'bg-green-100 border-l-4 border-green-500'
    };
    return colors[priority];
  };

  const getPriorityBadge = (priority) => {
    const badges = {
      high: 'bg-red-500 text-white',
      medium: 'bg-yellow-500 text-white',
      low: 'bg-green-500 text-white'
    };
    return badges[priority];
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-6">
      <div className="max-w-4xl mx-auto">
        <div className="text-center mb-8">
          <h1 className="text-4xl font-bold text-indigo-900 mb-2">ðŸ“š College Daily Planner</h1>
          <p className="text-gray-600">Stay organized, meet deadlines, achieve your goals</p>
        </div>
        {/* Dashboard Stats */}
        <div className="grid grid-cols-3 gap-4 mb-8">
          <div className="bg-white rounded-lg shadow p-4 text-center">
            <div className="text-3xl font-bold text-indigo-600">{todayTasks.length}</div>
            <div className="text-sm text-gray-600">Tasks Today</div>
          </div>
          <div className="bg-white rounded-lg shadow p-4 text-center">
            <div className="text-3xl font-bold text-green-600">{completedCount}</div>
            <div className="text-sm text-gray-600">Completed</div>
          </div>
          <div className="bg-white rounded-lg shadow p-4 text-center">
            <div className="text-3xl font-bold text-blue-600">{completionRate}%</div>
            <div className="text-sm text-gray-600">Progress</div>
          </div>
        </div>
        {/* Progress Bar */}
        <div className="bg-white rounded-lg shadow p-4 mb-8">
          <div className="flex justify-between items-center mb-2">
            <span className="font-semibold text-gray-700">Daily Progress</span>
            <span className="text-sm text-gray-600">{completedCount}/{todayTasks.length}</span>
          </div>
          <div className="w-full bg-gray-200 rounded-full h-3">
            <div className="bg-gradient-to-r from-green-400 to-green-600 h-3 rounded-full transition-all duration-500" style={{ width: `${completionRate}%` }}></div>
          </div>
        </div>
        {/* Add Task Form */}
        <div className="bg-white rounded-lg shadow p-6 mb-8">
          <h2 className="text-xl font-bold text-gray-800 mb-4">âž• Add New Task</h2>
          <div className="space-y-4">
            <input type="text" placeholder="What assignment do you need to complete?" value={newTask} onChange={(e) => setNewTask(e.target.value)} className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500" />
            <div className="grid grid-cols-2 gap-4">
              <input type="time" value={newTime} onChange={(e) => setNewTime(e.target.value)} className="px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500" />
              <input type="date" value={newDeadline} onChange={(e) => setNewDeadline(e.target.value)} className="px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500" />
            </div>
            <div className="flex gap-4">
              <select value={newPriority} onChange={(e) => setNewPriority(e.target.value)} className="flex-1 px-4 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500">
                <option value="high">ðŸ”´ High Priority</option>
                <option value="medium">ðŸŸ¡ Medium Priority</option>
                <option value="low">ðŸŸ¢ Low Priority</option>
              </select>
              <button onClick={addTask} className="bg-indigo-600 hover:bg-indigo-700 text-white font-semibold px-6 py-2 rounded-lg flex items-center gap-2 transition">
                <Plus size={20} /> Add Task
              </button>
            </div>
          </div>
        </div>
        {/* Task List */}
        <div className="bg-white rounded-lg shadow p-6">
          <h2 className="text-xl font-bold text-gray-800 mb-4">ðŸ“‹ Today's Tasks</h2>
          {todayTasks.length === 0 ? (
            <div className="text-center py-12">
              <div className="text-4xl mb-3">âœ¨</div>
              <p className="text-gray-500 text-lg">No tasks yet. Add one to get started!</p>
            </div>
          ) : (
            <div className="space-y-3">
              {todayTasks.map(task => (
                <div key={task.id} className={`p-4 rounded-lg ${getPriorityColor(task.priority)} flex items-center gap-4 transition hover:shadow-md`}>
                  <button onClick={() => toggleTask(task.id)} className={`flex-shrink-0 w-6 h-6 rounded border-2 flex items-center justify-center transition ${task.completed ? 'bg-green-500 border-green-500' : 'border-gray-400 hover:border-green-500'}`}>
                    {task.completed && <Check size={16} className="text-white" />}
                  </button>
                  <div className="flex-grow">
                    <div className="flex items-center gap-2 mb-1">
                      <span className={`text-sm font-semibold px-2 py-1 rounded ${getPriorityBadge(task.priority)}`}>{task.priority.toUpperCase()}</span>
                      <span className="text-sm font-semibold text-gray-700">{task.time}</span>
                      <span className="text-sm text-gray-600">Due: {new Date(task.deadline).toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}</span>
                    </div>
                    <p className={`text-gray-800 font-medium ${task.completed ? 'line-through text-gray-500' : ''}`}>{task.text}</p>
                  </div>
                  <button onClick={() => deleteTask(task.id)} className="text-red-500 hover:text-red-700 transition flex-shrink-0">
                    <Trash2 size={20} />
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>
        <div className="bg-indigo-50 border border-indigo-200 rounded-lg p-6 mt-8">
          <h3 className="font-bold text-indigo-900 mb-3">ðŸ’¡ Time Management Tips</h3>
          <ul className="text-sm text-indigo-800 space-y-2">
            <li>âœ“ Prioritize high-priority tasks first</li>
            <li>âœ“ Break large assignments into smaller tasks</li>
            <li>âœ“ Set realistic time estimates</li>
            <li>âœ“ Use time blocks to minimize distractions</li>
            <li>âœ“ Review your planner every morning</li>
          </ul>
        </div>
      </div>
    </div>
  );
}
